import React from "react";
import { Typography } from "@mui/material";

import "./styles.css";
import {useParams, Link} from "react-router-dom";
import models from "../../modelData/models";


/**
 * Define UserPhotos, a React component of Project 4.
 */
function UserPhotos () {
    const { userId } = useParams();
    const photo = models.photoOfUserModel(userId);
    console.log(photo);  
    if(!photo){
      return (
        <>
          <h3>Not Found</h3>
          <Link to={`/users/${userId}`}>Back</Link>
        </>
      )
    }
    return (
      <Typography variant="body1">
        <ul>
        {photo.map((item) =>(

          <div>
            <img src={require(`../../images/${item.file_name}`)} alt="" style={{width:"300px", height:"200px"}} />
            <div>
              <label>Created At: </label>
            <span style={{ color: "red", fontWeight: "bold" }}>{item.date_time}</span>
            </div> 
            <div>
              <h4>Comment</h4>
              {/* {item.comments.map((comment) =>(
                <div>
                  <label>Created At: </label>
                  <span style={{ color: "red", fontWeight: "bold" }}>{comment.date_time}</span>
                </div>
              ))

              } */}
              {console.log(item)}
            </div>
            <hr />
          </div>


        ))}
        </ul>
      </Typography>
    );
}

export default UserPhotos;
