import React from "react";
import {Typography} from "@mui/material";

import "./styles.css";
import {useParams} from "react-router-dom";
import models from "../../modelData/models";

/**
 * Define UserDetail, a React component of Project 4.
 */
function UserDetail() {
    const user = useParams();
    const users = models.userListModel();
    const userinfo = users.find((i) => (i._id === user.userId));
    if(!userinfo){
      return <alert>Invalid ID {user.userId}</alert>;
    }
    return (
        <>
          <Typography variant="body1">
            <ul>
              <li>{userinfo.first_name}</li>
              <li>{userinfo.last_name}</li>
              <li>{userinfo.location}</li>
              <textarea>{userinfo.description}</textarea>
              <li>{userinfo.occupation}</li>
            </ul>
          </Typography>
        </>

    );
}

export default UserDetail;
