import React from "react";
import { AppBar, Toolbar, Typography } from "@mui/material";
import { useLocation, useParams } from "react-router-dom";
import {useState, useEffect} from "react";
import models from "../../modelData/models";

import "./styles.css";

/**
 * Define TopBar, a React component of Project 4.
 */
function TopBar () {
  const [context, setContext] = useState("");
  const location = useLocation();
  const params = useParams();
  const path = location.pathname;

    if (path.startsWith("/users/") && !path.includes("/photos")) {
      const user = models.userModel(params.userId);
      setContext(user ? user.first_name + " " + user.last_name : "");
    } else if (path.startsWith("/photos/")) {
      const user = models.userModel(params.userId);
      setContext(user ? `Photos of ${user.first_name} ${user.last_name}` : "");
    } else {
      setContext("");
    }

    return (
      <AppBar className="topbar-appBar" position="absolute">
        <Toolbar>
          <Typography variant="h5" color="inherit">
            Tran Van Vu - B22DCAT318
          </Typography>
          <Typography variant="h6" color="inherit">
          {context}
        </Typography>
        </Toolbar>
      </AppBar>
    );
}

export default TopBar;
